package com.wb.web.portals.dto.opinionfeedback;

import java.util.Date;

public class OpinionCountDto {

	
	private Date feedbackTime; 		//反馈时间
	private String feedbackUser;  	//用户名
	private int num;				//次数
	private Short typeId = 0;		//类型   1：合理化建议征集 2：对智慧党建版块意见
	private long sort;
	
	public OpinionCountDto() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Date getFeedbackTime() {
		return feedbackTime;
	}
	public void setFeedbackTime(Date feedbackTime) {
		this.feedbackTime = feedbackTime;
	}
	
	public String getFeedbackUser() {
		return feedbackUser;
	}
	public void setFeedbackUser(String feedbackUser) {
		this.feedbackUser = feedbackUser;
	}
	public int getNum() {
		return num;
	}
	public void setNum(int num) {
		this.num = num;
	}
	public Short getTypeId() {
		return typeId;
	}
	public void setTypeId(Short typeId) {
		this.typeId = typeId;
	}
	public long getSort() {
		return sort;
	}
	public void setSort(long sort) {
		this.sort = sort;
	}
	
	
	
	

	
}
